import React, {Component} from 'react'


class Signin extends Component{
	constructor(props){
		super(props);

		this.state = {
			emailEntered: '',
			passwordEntered: ''
		} 
	}

	onEmailChange = (event) => {
		this.setState({emailEntered: event.target.value});
	}

	onPasswordChange = (event) => {
		this.setState({passwordEntered: event.target.value})
	}

	onSubmitSignIn = () => {
    fetch('http://localhost:3000/signin', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.emailEntered,
        password: this.state.passwordEntered
      })
    })
      .then(response => response.json())
      .then(user => {
        if (user === 'success') {
          // this.props.loadUser(user)
          this.props.onRouteChange('home');
        }
      })
  }

	render(){
		const {onRouteChange} = this.props;
		return (
			<article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l shadow-5 mw6 center ">
				<main className="pa4 black-80">
				  <form className="measure">
				    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
				      <legend className="f1 fw6 ph0 mh0">Sign In</legend>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" for="email-address">Email</label>
				        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
				        	type="email" name="email-address"  
			        		id="email-address"
			        		onChange = {this.onEmailChange}

		        		/>
				      </div>
				      <div className="mv3">
				        <label className="db fw6 lh-copy f6" for="password">Password</label>
				        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
				        	type="password" name="password"  
				        	id="password" 
				        	onChange = {this.onPasswordChange}
			        	/>
				      </div>
				    </fieldset>
				    <div className="">
				      <input 
				      		className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 	
				      		type="submit" value="Sign in"
		      				onClick = {this.onSubmit}
			      		 />

				    </div>
				    <div className="lh-copy mt3">
				      <p onClick = {() => onRouteChange('Register')} className="f6 link dim black db pointer">Register</p>
				    </div>
				  </form>
				</main>
			</article>
			)
		}
	}


export default Signin;
